return appointmentSources.map(source => ({ json: source }));

// Parse appointment availability and detect changes
const appointmentSource = $('Prepare Appointment Sources').item.json;
const responseData = $json;

// Handle HTTP request errors
if (!responseData || responseData.error) {
  return [{ 
    json: { 
      country: appointmentSource.country, 
      consulate: appointmentSource.consulate,
      appointments: [], 
      error: responseData?.error || 'HTTP request failed',
      checkedAt: new Date().toISOString()
    } 
  }];
}

const html = responseData.data || responseData.body || '';

if (!html || typeof html !== 'string') {
  return [{ 
    json: { 
      country: appointmentSource.country, 
      consulate: appointmentSource.consulate,
      appointments: [], 
      error: 'No HTML data received or invalid response',
      checkedAt: new Date().toISOString()
    } 
  }];
}

// Simple HTML parsing without external dependencies
const appointments = [];
const now = new Date();

try {
  // Basic text-based parsing for different appointment systems
  switch (appointmentSource.checkType) {
    case 'CALENDAR_SCAN':
      // US Consulate - look for common date patterns
      const dateMatches = html.match(/\b(\d{1,2}[\/\-]\d{1,2}[\/\-]\d{2,4})\b/g) || [];
      const timeMatches = html.match(/\b(\d{1,2}:\d{2}\s*(?:AM|PM|am|pm)?)\b/g) || [];

      // Look for available slots indicators
      const availableIndicators = [
        'available', 'open', 'book now', 'schedule',
        'disponÃ­vel', 'agendar', 'reservar'
      ];

      const hasAvailableSlots = availableIndicators.some(indicator => 
        html.toLowerCase().includes(indicator)
      );

      if (hasAvailableSlots && dateMatches.length > 0) {
        dateMatches.slice(0, 10).forEach((dateStr, index) => {
          try {
            const appointmentDate = new Date(dateStr);
            if (appointmentDate > now) {
              appointments.push({
                date: dateStr,
                time: timeMatches[index] || 'Time TBD',
                datetime: appointmentDate.toISOString(),
                type: 'AVAILABLE_SLOT',
                slotsRemaining: 1,
                lastSeen: now.toISOString(),
                source: 'text_parsing'
              });
            }
          } catch (e) {
            // Skip invalid dates
          }
        });
      }
      break;

    case 'SLOT_MONITORING':
      // VFS Global - look for slot availability
      const slotIndicators = html.match(/slot[s]?\s*available|\d+\s*slot[s]?/gi) || [];
      const vfsDateMatches = html.match(/\b(\d{1,2}[\/\-]\d{1,2}[\/\-]\d{2,4})\b/g) || [];

      if (slotIndicators.length > 0 && vfsDateMatches.length > 0) {
        vfsDateMatches.slice(0, 5).forEach(dateStr => {
          try {
            const appointmentDate = new Date(dateStr);
            if (appointmentDate > now) {
              appointments.push({
                date: dateStr,
                datetime: appointmentDate.toISOString(),
                type: 'MULTIPLE_SLOTS',
                slotsRemaining: slotIndicators.length,
                lastSeen: now.toISOString(),
                source: 'text_parsing'
              });
            }
          } catch (e) {
            // Skip invalid dates
          }
        });
      }
      break;

    case 'AVAILABILITY_CHECK':
      // TLS Contact - simple availability check
      const tlsDateMatches = html.match(/\b(\d{1,2}[\/\-]\d{1,2}[\/\-]\d{2,4})\b/g) || [];
      const hasBookingOptions = html.toLowerCase().includes('book') || 
                               html.toLowerCase().includes('available') ||
                               html.toLowerCase().includes('select');

      if (hasBookingOptions && tlsDateMatches.length > 0) {
        tlsDateMatches.slice(0, 5).forEach(dateStr => {
          try {
            const appointmentDate = new Date(dateStr);
            if (appointmentDate > now) {
              appointments.push({
                date: dateStr,
                datetime: appointmentDate.toISOString(),
                type: 'DAY_AVAILABLE',
                slotsRemaining: 'unknown',
                lastSeen: now.toISOString(),
                source: 'text_parsing'
              });
            }
          } catch (e) {
            // Skip invalid dates
          }
        });
      }
      break;
  }

  // Sort appointments by date
  appointments.sort((a, b) => new Date(a.datetime) - new Date(b.datetime));

  // Filter appointments within next 60 days (cancellation/reschedule window)
  const sixtyDaysFromNow = new Date(now.getTime() + 60 * 24 * 60 * 60 * 1000);
  const relevantAppointments = appointments.filter(apt => 
    new Date(apt.datetime) <= sixtyDaysFromNow
  );

  return [{
    json: {
      country: appointmentSource.country,
      consulate: appointmentSource.consulate,
      city: appointmentSource.city,
      visaTypes: appointmentSource.visaTypes,
      appointments: relevantAppointments,
      totalSlots: relevantAppointments.length,
      earliestDate: relevantAppointments[0]?.date || null,
      checkedAt: now.toISOString(),
      hasNewSlots: relevantAppointments.length > 0,
      parsingMethod: 'text_based',
      responseSize: html.length
    }
  }];

} catch (error) {
  return [{
    json: {
      country: appointmentSource.country,
      consulate: appointmentSource.consulate,
      appointments: [],
      error: `Parsing error: ${error.message}`,
      checkedAt: now.toISOString()
    }
  }];
}

