// Initialize database tables if they don't exist
const createTablesSQL = `
CREATE TABLE IF NOT EXISTS appointment_monitoring (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    country TEXT NOT NULL,
    consulate TEXT NOT NULL,
    city TEXT,
    visa_type TEXT,
    change_type TEXT,
    detected_at DATETIME,
    availability_window TEXT,
    appointment_data TEXT,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE IF NOT EXISTS clients (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL,
    email TEXT,
    phone TEXT,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE IF NOT EXISTS appointment_waitlist (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    client_id INTEGER,
    country TEXT NOT NULL,
    consulate TEXT NOT NULL,
    preferred_date_range_start DATE,
    preferred_date_range_end DATE,
    notification_methods TEXT DEFAULT '["whatsapp"]',
    max_notice_hours INTEGER DEFAULT 24,
    priority INTEGER DEFAULT 1,
    status TEXT DEFAULT 'ACTIVE',
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (client_id) REFERENCES clients(id)
);
`;

// Execute table creation (this will be ignored if tables exist)
try {
  // This is just a placeholder - actual table creation should be done via SQLite node
  console.log('Tables should be created via separate SQLite node');
} catch (e) {
  console.log('Table creation handled elsewhere');
}

// Detect new appointments and cancellations
const currentData = $('Parse Appointment Data').item.json;
const previousQueryResult = $input.all();

let changeDetected = false;
let newAppointments = [];
let changes = [];
let previousData = null;

// Handle case where previous query returned results
if (previousQueryResult.length > 0 && previousQueryResult[0].json) {
  previousData = previousQueryResult[0].json;
}

if (!previousData || !previousData.appointment_data) {
  // First time checking - all appointments are "new"
  changeDetected = currentData.hasNewSlots;
  newAppointments = currentData.appointments || [];

  if (newAppointments.length > 0) {
    changes.push({
      type: 'INITIAL_SCAN',
      description: `Initial scan found ${currentData.totalSlots} available slots`,
      appointments: newAppointments
    });
  }
} else {
  // Compare with previous scan
  let prevAppointments = [];
  try {
    prevAppointments = JSON.parse(previousData.appointment_data || '[]');
  } catch (e) {
    prevAppointments = [];
  }

  const currentAppointments = currentData.appointments || [];

  // Find truly new appointments (within 30 days - likely cancellations)
  const thirtyDaysFromNow = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000);

  newAppointments = currentAppointments.filter(current => {
    // Check if this appointment slot is within 30 days (priority window)
    const appointmentDate = new Date(current.datetime);
    const isWithinPriorityWindow = appointmentDate <= thirtyDaysFromNow;

    // Check if this slot existed in previous scan
    const existedBefore = prevAppointments.some(prev => 
      prev.date === current.date && 
      (prev.time === current.time || (!prev.time && !current.time))
    );

    return isWithinPriorityWindow && !existedBefore;
  });

  // Detect cancellations (slots that disappeared)
  const cancelledAppointments = prevAppointments.filter(prev => {
    const stillExists = currentAppointments.some(current => 
      current.date === prev.date && 
      (current.time === prev.time || (!current.time && !prev.time))
    );
    return !stillExists;
  });

  if (newAppointments.length > 0) {
    changeDetected = true;
    changes.push({
      type: 'NEW_SLOTS_DETECTED',
      description: `${newAppointments.length} new appointment slots detected`,
      appointments: newAppointments
    });
  }

  if (cancelledAppointments.length > 0) {
    changes.push({
      type: 'SLOTS_CANCELLED',
      description: `${cancelledAppointments.length} appointment slots no longer available`,
      appointments: cancelledAppointments
    });
  }
}

// Only proceed if we detected meaningful changes
if (changeDetected && newAppointments.length > 0) {
  return [{
    json: {
      ...currentData,
      newAppointments,
      changes,
      changeDetected: true,
      prioritySlots: newAppointments.filter(apt => {
        const appointmentDate = new Date(apt.datetime);
        const sevenDaysFromNow = new Date(Date.now() + 7 * 24 * 60 * 60 * 1000);
        return appointmentDate <= sevenDaysFromNow;
      }),
      previousScan: previousData?.detected_at || null,
      scanComparison: {
        previousSlots: previousData ? JSON.parse(previousData.appointment_data || '[]').length : 0,
        currentSlots: currentData.totalSlots,
        newSlots: newAppointments.length
      }
    }
  }];
} else {
  // No significant changes, but still log the scan
  return [{
    json: {
      ...currentData,
      newAppointments: [],
      changes: [],
      changeDetected: false,
      prioritySlots: [],
      scanType: 'NO_CHANGES'
    }
  }];
}

