      status: 'ACTIVE'
    }
  }
];

if (!interestedClientsData || interestedClientsData.length === 0) {
  return [{
    json: {
      message: 'No interested clients found for this consulate',
      country: appointmentData.country,
      consulate: appointmentData.consulate,
      newSlots: appointmentData.newAppointments?.length || 0
    }
  }];
}

const interestedClients = interestedClientsData.map(item => item.json);
const notifications = [];
const prioritySlots = appointmentData.prioritySlots || [];
const allNewSlots = appointmentData.newAppointments || [];

// Sort clients by priority and filter by their preferences
const eligibleClients = interestedClients.filter(client => {
  // Check if any new slots match client's preferred date range
  if (client.preferred_date_range_start && client.preferred_date_range_end) {
    const preferredStart = new Date(client.preferred_date_range_start);
    const preferredEnd = new Date(client.preferred_date_range_end);

    return allNewSlots.some(slot => {
      const slotDate = new Date(slot.datetime);
      return slotDate >= preferredStart && slotDate <= preferredEnd;
    });
  }

  // If no specific range, include all clients
  return true;
});

for (const client of eligibleClients) {
  const relevantSlots = allNewSlots.filter(slot => {
    if (client.preferred_date_range_start && client.preferred_date_range_end) {
      const preferredStart = new Date(client.preferred_date_range_start);
      const preferredEnd = new Date(client.preferred_date_range_end);
      const slotDate = new Date(slot.datetime);
      return slotDate >= preferredStart && slotDate <= preferredEnd;
    }
    return true;
  });

  if (relevantSlots.length === 0) continue;

  const earliestSlot = relevantSlots[0];
  const isPriority = prioritySlots.length > 0;

  // Create urgency-based message
  let urgencyLevel = 'NORMAL';
  let urgencyEmoji = 'ðŸ“…';
  let urgencyText = 'Nova vaga disponÃ­vel';

  if (isPriority) {
    urgencyLevel = 'URGENT';
    urgencyEmoji = 'ðŸš¨';
    urgencyText = 'VAGA URGENTE! PrÃ³ximos 7 dias';
  } else if (relevantSlots.length === 1) {
    urgencyLevel = 'HIGH';
    urgencyEmoji = 'âš¡';
    urgencyText = 'Vaga limitada disponÃ­vel';
  }

  const whatsappMessage = `${urgencyEmoji} *${urgencyText.toUpperCase()}*\n\n` +
    `ðŸ›ï¸ *${appointmentData.consulate}*\n` +
    `ðŸŒ *PaÃ­s:* ${appointmentData.country}\n` +
    `ðŸ“ *Cidade:* ${appointmentData.city}\n\n` +
    `ðŸ“… *Datas DisponÃ­veis:*\n${relevantSlots.slice(0, 5).map(slot => {
      const date = new Date(slot.datetime).toLocaleDateString('pt-BR');
      const time = slot.time && slot.time !== 'Time TBD' ? ` Ã s ${slot.time}` : '';
      return `â€¢ ${date}${time}`;
    }).join('\n')}\n\n` +
    `${relevantSlots.length > 5 ? `...e mais ${relevantSlots.length - 5} datas\n\n` : ''}` +
    `âš¡ *AÃ‡ÃƒO RÃPIDA NECESSÃRIA!*\n` +
    `ðŸ“ž Responda "SIM" para agendar AGORA\n` +
    `â° Ou ligue: (11) 99999-9999\n\n` +
    `ðŸ’¨ Vagas se esgotam rapidamente!`;

  // Determine notification methods
  let notificationMethods = ['whatsapp'];
  try {
    if (client.notification_methods) {
      notificationMethods = JSON.parse(client.notification_methods);
    }
  } catch (e) {
    notificationMethods = ['whatsapp'];
  }

  notifications.push({
    json: {
      clientId: client.client_id || client.id,
      clientName: client.name || 'Cliente',
      clientEmail: client.email,
      clientPhone: client.phone,
      urgencyLevel,
      whatsappMessage,
      notificationMethods,
      appointmentData: {
        country: appointmentData.country,
        consulate: appointmentData.consulate,
        city: appointmentData.city,
        totalNewSlots: allNewSlots.length,
        prioritySlots: prioritySlots.length,
        relevantSlots: relevantSlots.length,
        earliestDate: earliestSlot.date,
        earliestTime: earliestSlot.time
      },
      maxNoticeHours: client.max_notice_hours || 24,
      priority: client.priority || 1,
      waitlistId: client.id
    }
  });
}

// Sort notifications by client priority
notifications.sort((a, b) => b.json.priority - a.json.priority);

if (notifications.length === 0) {
  return [{
    json: {
      message: 'No eligible clients found for the available slots',
      totalClients: interestedClients.length,
      eligibleClients: eligibleClients.length,
      newSlots: allNewSlots.length
    }
  }];
}

return notifications;
