// Simplified news parsing without external dependencies
const results = [];

for (const item of $input.all()) {
  try {
    const body = item.json.body || item.json.data || '';
    const url = item.json.url || '';

    // Determine country and category from URL
    let country = 'Global';
    let flag = 'üåç';
    let category = 'Not√≠cias Gerais';

    if (url.includes('uscis.gov')) {
      country = 'Estados Unidos';
      flag = 'üá∫üá∏';
      category = 'Not√≠cias Urgentes';
    } else if (url.includes('canada.ca')) {
      country = 'Canad√°';
      flag = 'üá®üá¶';
      category = 'Imigra√ß√£o';
    } else if (url.includes('homeaffairs.gov.au')) {
      country = 'Austr√°lia';
      flag = 'üá¶üá∫';
      category = 'Vistos Trabalho';
    } else if (url.includes('gov.br')) {
      country = 'Brasil';
      flag = 'üáßüá∑';
      category = 'Pol√≠tica Migrat√≥ria';
    } else if (url.includes('gov.uk')) {
      country = 'Reino Unido';
      flag = 'üá¨üáß';
      category = 'Brexit e Vistos';
    } else if (url.includes('bamf.de')) {
      country = 'Alemanha';
      flag = 'üá©üá™';
      category = 'Uni√£o Europeia';
    } else if (url.includes('service-public.fr')) {
      country = 'Fran√ßa';
      flag = 'üá´üá∑';
      category = 'Vistos Europa';
    }

    // Simple HTML parsing using regex for basic extraction
    const titleMatch = body.match(/<title[^>]*>([^<]+)<\/title>/i);
    const title = titleMatch ? titleMatch[1].trim() : 'Not√≠cia de Imigra√ß√£o';

    // Extract some content from common HTML patterns
    const contentMatches = body.match(/<p[^>]*>([^<]+)<\/p>/gi) || [];
    let content = contentMatches.slice(0, 3).map(p => p.replace(/<[^>]*>/g, '')).join(' ');

    if (!content) {
      content = 'Atualiza√ß√£o importante sobre imigra√ß√£o para ' + country;
    }

    const excerpt = content.substring(0, 300) + '...';

    // Check for immigration-related keywords
    const keywords = ['visa', 'immigration', 'citizen', 'permit', 'resident', 'entry', 'border'];
    const hasRelevantContent = keywords.some(keyword => 
      title.toLowerCase().includes(keyword) || content.toLowerCase().includes(keyword)
    );

    // Check for urgency
    const urgentKeywords = ['urgent', 'immediate', 'emergency', 'breaking', 'alert'];
    const isUrgent = urgentKeywords.some(keyword => 
      title.toLowerCase().includes(keyword) || content.toLowerCase().includes(keyword)
    );

    if (hasRelevantContent || country !== 'Global') {
      results.push({
        title: title,
        excerpt: excerpt,
        content: content,
        category: category,
        author: 'Fonte Oficial ' + country,
        tags: [country, 'Not√≠cia Oficial', isUrgent ? 'Urgente' : 'Atualiza√ß√£o'],
        country: country,
        flag: flag,
        difficulty: 'Intermedi√°rio',
        type: 'Not√≠cia',
        sourceUrl: url,
        urgent: isUrgent,
        trending: false,
        publishDate: new Date().toISOString()
      });
    }

  } catch (error) {
    console.error('Error parsing content:', error);
    // Create a fallback item
    results.push({
      title: 'Atualiza√ß√£o de Imigra√ß√£o',
      excerpt: 'Nova informa√ß√£o dispon√≠vel sobre processos de imigra√ß√£o.',
      content: 'Informa√ß√£o atualizada sobre imigra√ß√£o.',
      category: 'Not√≠cias Gerais',
      author: 'Fonte Oficial',
      tags: ['Global', 'Atualiza√ß√£o'],
      country: 'Global',
      flag: 'üåç',
      difficulty: 'Intermedi√°rio',
      type: 'Not√≠cia',
      sourceUrl: '',
      urgent: false,
      trending: false,
      publishDate: new Date().toISOString()
    });
  }
}

return results;

// Filter out duplicates and old news
const filtered = [];
const seenTitles = new Set();

for (const item of $input.all()) {
  const title = item.json.title;

  // Skip duplicates
  if (seenTitles.has(title)) {
    continue;
  }
  seenTitles.add(title);

  // Skip old news (older than 7 days)
  const publishDate = new Date(item.json.publishDate || new Date());
  const weekAgo = new Date();
  weekAgo.setDate(weekAgo.getDate() - 7);

  if (publishDate < weekAgo) {
    continue;
  }

  // Skip if content is too short
  if (item.json.content.length < 100) {
    continue;
  }

  filtered.push(item.json);
}

// Sort by urgency and date
filtered.sort((a, b) => {
  if (a.urgent && !b.urgent) return -1;
  if (!a.urgent && b.urgent) return 1;
  return new Date(b.publishDate) - new Date(a.publishDate);
});

// Limit to 10 most important articles per run
return filtered.slice(0, 10);

