// Log monitoring activity
const timestamp = new Date().toISOString();
const total = $input.all().length;
const urgent = $input.all().filter(item => item.json.urgent).length;

console.log(`[${timestamp}] Blog monitoring completed:`);
console.log(`- Total articles processed: ${total}`);
console.log(`- Urgent articles: ${urgent}`);
console.log(`- Regular articles: ${total - urgent}`);

return {
  timestamp,
  total,
  urgent,
  success: true,
  articles: $input.all().map(item => ({
    title: item.json.title,
    country: item.json.country,
    urgent: item.json.urgent,
    category: item.json.category
  }))
};
