// Consolidate all metrics into comprehensive dashboard data
const leadMetrics = $('fetch-lead-metrics').item.json;
const clientMetrics = $('fetch-client-metrics').item.json;
const revenueMetrics = $('fetch-revenue-metrics').item.json;
const automationMetrics = $('fetch-automation-metrics').item.json;
const countryPerformance = $('fetch-country-performance').all().map(item => item.json);
const dailyTrends = $('fetch-daily-trends').all().map(item => item.json);
const consultantPerformance = $('fetch-consultant-performance').all().map(item => item.json);

// Calculate conversion rates
const leadToClientRate = leadMetrics.total_leads > 0 ? 
  (clientMetrics.new_clients_month / leadMetrics.leads_this_month * 100).toFixed(1) : 0;

const paymentConversionRate = clientMetrics.total_clients > 0 ?
  (clientMetrics.paid_clients / clientMetrics.total_clients * 100).toFixed(1) : 0;

// Calculate growth rates (comparing to previous period)
const weeklyGrowthRate = leadMetrics.leads_this_week > 0 && leadMetrics.leads_this_month > 0 ?
  (((leadMetrics.leads_this_week * 4) / leadMetrics.leads_this_month - 1) * 100).toFixed(1) : 0;

// Automation efficiency
const automationSuccessRate = automationMetrics.total_automations > 0 ?
  (automationMetrics.executed_automations / automationMetrics.total_automations * 100).toFixed(1) : 0;

// Revenue metrics
const avgRevenuePerClient = clientMetrics.paid_clients > 0 ?
  (revenueMetrics.total_revenue / clientMetrics.paid_clients).toFixed(0) : 0;

// Create comprehensive dashboard
const dashboardData = {
  generated_at: new Date().toISOString(),
  period: 'Last 90 days',

  // Key Performance Indicators
  kpis: {
    total_leads: leadMetrics.total_leads || 0,
    total_clients: clientMetrics.total_clients || 0,
    total_revenue: revenueMetrics.total_revenue || 0,
    avg_lead_score: parseFloat(leadMetrics.avg_score || 0).toFixed(1),
    lead_to_client_rate: leadToClientRate,
    payment_conversion_rate: paymentConversionRate,
    avg_revenue_per_client: avgRevenuePerClient,
    automation_success_rate: automationSuccessRate
  },

  // Weekly Performance
  weekly: {
    leads: leadMetrics.leads_this_week || 0,
    revenue: revenueMetrics.revenue_this_week || 0,
    automations: automationMetrics.automations_this_week || 0,
    growth_rate: weeklyGrowthRate
  },

  // Monthly Performance
  monthly: {
    leads: leadMetrics.leads_this_month || 0,
    new_clients: clientMetrics.new_clients_month || 0,
    revenue: revenueMetrics.revenue_this_month || 0,
    avg_payment: parseFloat(revenueMetrics.avg_payment || 0).toFixed(0)
  },

  // Client Status Breakdown
  client_status: {
    active: clientMetrics.active_clients || 0,
    paid: clientMetrics.paid_clients || 0,
    documents_pending: clientMetrics.docs_pending || 0,
    total: clientMetrics.total_clients || 0
  },

  // Automation Insights
  automation: {
    total_interventions: automationMetrics.total_automations || 0,
    executed: automationMetrics.executed_automations || 0,
    urgent_cases: automationMetrics.urgent_interventions || 0,
    success_rate: automationSuccessRate
  },

  // Top Performing Countries
  top_countries: countryPerformance.slice(0, 5).map(country => ({
    country: country.target_country,
    clients: country.client_count,
    avg_revenue: parseFloat(country.avg_revenue || 0).toFixed(0)
  })),

  // Daily Trends (Last 7 days)
  daily_trends: dailyTrends.slice(0, 7).map(day => ({
    date: day.date,
    leads: day.leads,
    hot_leads: day.hot_leads,
    conversion_rate: day.leads > 0 ? (day.hot_leads / day.leads * 100).toFixed(1) : 0
  })),

  // Consultant Performance
  consultant_performance: consultantPerformance.slice(0, 5).map(consultant => ({
    name: consultant.consultant_name,
    leads_assigned: consultant.leads_assigned || 0,
    avg_lead_score: parseFloat(consultant.avg_lead_score || 0).toFixed(1),
    converted_clients: consultant.converted_clients || 0,
    conversion_rate: consultant.leads_assigned > 0 ? 
      (consultant.converted_clients / consultant.leads_assigned * 100).toFixed(1) : 0
  })),

  // Health Indicators
  health_indicators: {
    pipeline_health: leadMetrics.avg_score >= 60 ? 'GOOD' : leadMetrics.avg_score >= 40 ? 'FAIR' : 'POOR',
    automation_health: automationSuccessRate >= 80 ? 'EXCELLENT' : automationSuccessRate >= 60 ? 'GOOD' : 'NEEDS_ATTENTION',
    revenue_health: weeklyGrowthRate > 0 ? 'GROWING' : weeklyGrowthRate > -10 ? 'STABLE' : 'DECLINING',
    client_satisfaction: clientMetrics.docs_pending < clientMetrics.total_clients * 0.3 ? 'HIGH' : 'MODERATE'
  },

  // Alerts and Recommendations
  alerts: [],
  recommendations: []
};

// Generate dynamic alerts
if (leadMetrics.leads_this_week < leadMetrics.leads_this_month / 4 * 0.7) {
  dashboardData.alerts.push({
    type: 'WARNING',
    message: 'Lead generation abaixo da m√©dia semanal',
    priority: 'HIGH'
  });
}

if (automationMetrics.urgent_interventions > automationMetrics.total_automations * 0.4) {
  dashboardData.alerts.push({
    type: 'ATTENTION',
    message: 'Alto n√∫mero de interven√ß√µes urgentes - revisar processos',
    priority: 'MEDIUM'
  });
}

if (clientMetrics.docs_pending > clientMetrics.total_clients * 0.4) {
  dashboardData.alerts.push({
    type: 'ACTION_REQUIRED',
    message: 'Muitos clientes com documentos pendentes',
    priority: 'HIGH'
  });
}

// Generate recommendations
if (parseFloat(leadToClientRate) < 15) {
  dashboardData.recommendations.push('Melhorar qualifica√ß√£o de leads - taxa de convers√£o baixa');
}

if (parseFloat(paymentConversionRate) < 70) {
  dashboardData.recommendations.push('Otimizar processo de pagamento - muitos clientes n√£o pagam');
}

if (parseFloat(automationSuccessRate) < 80) {
  dashboardData.recommendations.push('Revisar automa√ß√µes - taxa de sucesso pode melhorar');
}

return [{ json: dashboardData }];

