// Extract and validate lead data
const body = $json.body;
const headers = $json.headers;

// Validate webhook secret
if (headers['x-webhook-secret'] !== 'visa2any_webhook_secret_2024') {
  throw new Error('Invalid webhook secret');
}

const leadData = {
  leadId: body.leadId || `LEAD_${Date.now()}`,
  name: body.name,
  email: body.email,
  phone: body.phone,
  cpf: body.cpf || null,
  targetCountry: body.targetCountry || 'UNKNOWN',
  visaType: body.visaType || 'TOURIST',
  budget: body.budget || null,
  urgency: body.urgency || 'MEDIUM',
  source: body.source || 'WEBSITE',
  utm_source: body.utm_source || null,
  utm_medium: body.utm_medium || null,
  utm_campaign: body.utm_campaign || null,
  referralCode: body.referralCode || null,
  socialMedia: body.socialMedia || null,
  createdAt: new Date().toISOString()
};

// Validate required fields
const requiredFields = ['name', 'email'];
for (const field of requiredFields) {
  if (!leadData[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Initial scoring based on available data
let initialScore = 50; // Base score

if (leadData.phone) initialScore += 10;
if (leadData.cpf) initialScore += 15;
if (leadData.budget && leadData.budget !== 'LOW') initialScore += 10;
if (leadData.urgency === 'HIGH') initialScore += 15;
if (['USA', 'CANADA', 'AUSTRALIA', 'UK'].includes(leadData.targetCountry)) initialScore += 10;

return [{ json: { ...leadData, initialScore } }];

// Consolidate all enrichment data
const leadData = $('validate-lead-data').item.json;
const serasaData = $('serasa-credit-check').item?.json || null;
const spcData = $('spc-score-check').item?.json || null;
const facebookData = $('facebook-profile-search').item?.json || null;
const linkedinData = $('linkedin-profile-search').item?.json || null;

// Process credit data
let creditProfile = {
  score: null,
  classification: 'UNKNOWN',
  restrictions: false,
  income_estimate: null
};

if (serasaData && !serasaData.error) {
  creditProfile.score = serasaData.score || null;
  creditProfile.classification = serasaData.classification || 'UNKNOWN';
  creditProfile.restrictions = serasaData.restrictions || false;
  creditProfile.income_estimate = serasaData.estimated_income || null;
}

if (spcData && !spcData.error && !creditProfile.score) {
  creditProfile.score = spcData.score || null;
  creditProfile.classification = spcData.risk_class || 'UNKNOWN';
}

// Process social media data
let socialProfile = {
  facebook: null,
  linkedin: null,
  education_level: 'UNKNOWN',
  employment_status: 'UNKNOWN',
  relationship_status: 'UNKNOWN',
  location: null
};

if (facebookData && facebookData.data && facebookData.data.length > 0) {
  const fbProfile = facebookData.data[0];
  socialProfile.facebook = {
    name: fbProfile.name,
    location: fbProfile.location?.name || null,
    education: fbProfile.education || null,
    work: fbProfile.work || null,
    relationship: fbProfile.relationship_status || null
  };

  if (fbProfile.education && fbProfile.education.length > 0) {
    const highestEd = fbProfile.education[0];
    if (highestEd.type === 'Graduate School') socialProfile.education_level = 'POST_GRADUATE';
    else if (highestEd.type === 'College') socialProfile.education_level = 'GRADUATE';
    else socialProfile.education_level = 'HIGH_SCHOOL';
  }

  if (fbProfile.work && fbProfile.work.length > 0) {
    socialProfile.employment_status = 'EMPLOYED';
  }
}

if (linkedinData && !linkedinData.error) {
  socialProfile.linkedin = {
    headline: linkedinData.headline || null,
    industry: linkedinData.industry || null,
    location: linkedinData.location?.name || null,
    positions: linkedinData.positions || null
  };

  if (linkedinData.positions && linkedinData.positions.length > 0) {
    socialProfile.employment_status = 'EMPLOYED';
  }
}

// Calculate enriched lead score
let enrichedScore = leadData.initialScore;

// Credit scoring impact
if (creditProfile.score) {
  if (creditProfile.score >= 700) enrichedScore += 20;
  else if (creditProfile.score >= 500) enrichedScore += 10;
  else if (creditProfile.score >= 300) enrichedScore += 5;
  else enrichedScore -= 10;
}

if (creditProfile.restrictions) enrichedScore -= 15;
if (creditProfile.income_estimate && creditProfile.income_estimate > 5000) enrichedScore += 15;

// Social profile impact
if (socialProfile.education_level === 'POST_GRADUATE') enrichedScore += 15;
else if (socialProfile.education_level === 'GRADUATE') enrichedScore += 10;

if (socialProfile.employment_status === 'EMPLOYED') enrichedScore += 10;
if (socialProfile.linkedin) enrichedScore += 5;
if (socialProfile.facebook) enrichedScore += 3;

// Determine lead quality
let leadQuality = 'LOW';
if (enrichedScore >= 80) leadQuality = 'HOT';
else if (enrichedScore >= 65) leadQuality = 'WARM';
else if (enrichedScore >= 50) leadQuality = 'MEDIUM';

// Assign ideal consultant type
let consultantType = 'JUNIOR';
if (leadQuality === 'HOT') consultantType = 'SENIOR';
else if (leadQuality === 'WARM') consultantType = 'MID_LEVEL';

// Determine priority level
let priority = 'MEDIUM';
if (leadQuality === 'HOT') priority = 'URGENT';
else if (leadQuality === 'WARM') priority = 'HIGH';

return [{
  json: {
    ...leadData,
    enrichment: {
      creditProfile,
      socialProfile,
      enrichedScore,
      leadQuality,
      consultantType,
      priority,
      enrichedAt: new Date().toISOString(),
      dataQuality: {
        hasCredit: !!creditProfile.score,
        hasSocial: !!(socialProfile.facebook || socialProfile.linkedin),
        completeness: calculateCompleteness(leadData, creditProfile, socialProfile)
      }
    }
  }
}];

function calculateCompleteness(lead, credit, social) {
  let score = 0;
  let total = 10;

  if (lead.name) score++;
  if (lead.email) score++;
  if (lead.phone) score++;
  if (lead.cpf) score++;
  if (lead.targetCountry !== 'UNKNOWN') score++;
  if (credit.score) score++;
  if (credit.income_estimate) score++;
  if (social.education_level !== 'UNKNOWN') score++;
  if (social.employment_status !== 'UNKNOWN') score++;
  if (social.linkedin || social.facebook) score++;

  return Math.round((score / total) * 100);
}

