// Extract and validate orchestration request
const body = $json.body;
const headers = $json.headers;

// Validate webhook secret
if (headers['x-webhook-secret'] !== 'visa2any_webhook_secret_2024') {
  throw new Error('Invalid webhook secret');
}

const orchestrationData = {
  clientId: body.clientId,
  clientName: body.clientName,
  clientEmail: body.clientEmail,
  clientPhone: body.clientPhone,
  trigger: body.trigger, // HIGH_SCORE, PAYMENT_PENDING, NO_RESPONSE, etc.
  priority: body.priority || 'MEDIUM', // URGENT, HIGH, MEDIUM, LOW
  context: body.context || {}, // Additional context data
  targetCountry: body.targetCountry || null,
  visaType: body.visaType || null,
  serviceLevel: body.serviceLevel || 'STANDARD',
  lastContact: body.lastContact || null,
  preferredChannel: body.preferredChannel || 'AUTO',
  timeZone: body.timeZone || 'America/Sao_Paulo',
  orchestrationId: `ORCH_${Date.now()}_${body.clientId}`,
  createdAt: new Date().toISOString()
};

// Validate required fields
const requiredFields = ['clientId', 'clientName', 'trigger'];
for (const field of requiredFields) {
  if (!orchestrationData[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

return [{ json: orchestrationData }];

// Intelligent channel orchestration strategy
const orchestrationData = $('validate-orchestration-data').item.json;
const commHistory = $input.all().map(item => item.json);

// Analyze communication patterns
const channelStats = {
  whatsapp: { sent: 0, responded: 0, lastResponse: null },
  email: { sent: 0, responded: 0, lastResponse: null },
  sms: { sent: 0, responded: 0, lastResponse: null },
  phone: { attempted: 0, connected: 0, lastCall: null }
};

commHistory.forEach(comm => {
  if (channelStats[comm.channel]) {
    channelStats[comm.channel].sent++;
    if (comm.response_received) {
      channelStats[comm.channel].responded++;
      if (!channelStats[comm.channel].lastResponse || 
          new Date(comm.created_at) > new Date(channelStats[comm.channel].lastResponse)) {
        channelStats[comm.channel].lastResponse = comm.created_at;
      }
    }
  }
});

// Calculate response rates
Object.keys(channelStats).forEach(channel => {
  const stats = channelStats[channel];
  stats.responseRate = stats.sent > 0 ? (stats.responded / stats.sent) * 100 : 0;
  stats.daysSinceLastResponse = stats.lastResponse ? 
    Math.floor((new Date() - new Date(stats.lastResponse)) / (1000 * 60 * 60 * 24)) : 999;
});

// Determine orchestration strategy based on trigger and history
let orchestrationStrategy = {
  sequence: [],
  totalDuration: 0, // hours
  expectedChannels: 0,
  escalationPath: []
};

switch (orchestrationData.trigger) {
  case 'HIGH_SCORE_LEAD':
    orchestrationStrategy = createHighScoreStrategy(orchestrationData, channelStats);
    break;
  case 'PAYMENT_PENDING':
    orchestrationStrategy = createPaymentStrategy(orchestrationData, channelStats);
    break;
  case 'NO_RESPONSE_FOLLOWUP':
    orchestrationStrategy = createFollowUpStrategy(orchestrationData, channelStats);
    break;
  case 'DOCUMENT_REMINDER':
    orchestrationStrategy = createDocumentStrategy(orchestrationData, channelStats);
    break;
  case 'CONSULTATION_REMINDER':
    orchestrationStrategy = createConsultationStrategy(orchestrationData, channelStats);
    break;
  case 'VISA_APPROVED_CELEBRATION':
    orchestrationStrategy = createCelebrationStrategy(orchestrationData, channelStats);
    break;
  default:
    orchestrationStrategy = createStandardStrategy(orchestrationData, channelStats);
}

// Functions to create different strategies
function createHighScoreStrategy(data, stats) {
  const bestChannel = getBestChannel(stats);
  return {
    sequence: [
      { channel: 'whatsapp', delay: 0, type: 'IMMEDIATE_CONTACT', priority: 'URGENT' },
      { channel: 'email', delay: 2, type: 'DETAILED_PROPOSAL', priority: 'HIGH' },
      { channel: 'phone', delay: 24, type: 'PERSONAL_CONSULTATION', priority: 'HIGH' },
      { channel: 'sms', delay: 48, type: 'FINAL_OFFER', priority: 'MEDIUM' }
    ],
    totalDuration: 72,
    expectedChannels: 4,
    escalationPath: ['whatsapp', 'phone', 'email', 'sms']
  };
}

function createPaymentStrategy(data, stats) {
  return {
    sequence: [
      { channel: 'whatsapp', delay: 0, type: 'PAYMENT_REMINDER', priority: 'URGENT' },
      { channel: 'email', delay: 4, type: 'PAYMENT_LINK', priority: 'URGENT' },
      { channel: 'phone', delay: 24, type: 'PAYMENT_ASSISTANCE', priority: 'HIGH' },
      { channel: 'sms', delay: 48, type: 'PAYMENT_FINAL_NOTICE', priority: 'URGENT' },
      { channel: 'phone', delay: 72, type: 'MANAGER_ESCALATION', priority: 'URGENT' }
    ],
    totalDuration: 96,
    expectedChannels: 4,
    escalationPath: ['whatsapp', 'email', 'phone', 'manager']
  };
}

function createFollowUpStrategy(data, stats) {
  const responseRate = Object.values(stats).reduce((acc, s) => acc + s.responseRate, 0) / 4;
  const sequence = responseRate > 50 ? [
    { channel: getBestChannel(stats), delay: 0, type: 'GENTLE_FOLLOWUP', priority: 'MEDIUM' },
    { channel: 'email', delay: 24, type: 'VALUE_REMINDER', priority: 'MEDIUM' },
    { channel: 'whatsapp', delay: 72, type: 'FINAL_ATTEMPT', priority: 'LOW' }
  ] : [
    { channel: 'whatsapp', delay: 0, type: 'REENGAGEMENT', priority: 'MEDIUM' },
    { channel: 'email', delay: 12, type: 'SPECIAL_OFFER', priority: 'MEDIUM' },
    { channel: 'phone', delay: 48, type: 'PERSONAL_OUTREACH', priority: 'MEDIUM' }
  ];

  return {
    sequence,
    totalDuration: 96,
    expectedChannels: 3,
    escalationPath: [getBestChannel(stats), 'email', 'phone']
  };
}

function createDocumentStrategy(data, stats) {
  return {
