    escalationPath: ['whatsapp', 'email', 'phone']
  };
}

function createConsultationStrategy(data, stats) {
  return {
    sequence: [
      { channel: 'whatsapp', delay: 0, type: 'CONSULTATION_REMINDER', priority: 'HIGH' },
      { channel: 'email', delay: 2, type: 'CONSULTATION_DETAILS', priority: 'HIGH' },
      { channel: 'sms', delay: 8, type: 'CONSULTATION_CONFIRMATION', priority: 'MEDIUM' },
      { channel: 'phone', delay: 1, type: 'CONSULTATION_CALL', priority: 'HIGH' } // 1 hour before
    ],
    totalDuration: 24,
    expectedChannels: 4,
    escalationPath: ['whatsapp', 'email', 'phone']
  };
}

function createCelebrationStrategy(data, stats) {
  return {
    sequence: [
      { channel: 'whatsapp', delay: 0, type: 'VISA_CELEBRATION', priority: 'HIGH' },
      { channel: 'email', delay: 1, type: 'DETAILED_CONGRATULATIONS', priority: 'MEDIUM' },
      { channel: 'phone', delay: 24, type: 'CELEBRATION_CALL', priority: 'MEDIUM' },
      { channel: 'email', delay: 168, type: 'UPSELL_OPPORTUNITY', priority: 'LOW' } // 1 week later
    ],
    totalDuration: 192, // 8 days
    expectedChannels: 3,
    escalationPath: ['whatsapp', 'email', 'phone']
  };
}

function createStandardStrategy(data, stats) {
  return {
    sequence: [
      { channel: getBestChannel(stats), delay: 0, type: 'INITIAL_CONTACT', priority: 'MEDIUM' },
      { channel: 'email', delay: 24, type: 'FOLLOWUP_EMAIL', priority: 'MEDIUM' },
      { channel: 'whatsapp', delay: 72, type: 'FINAL_ATTEMPT', priority: 'LOW' }
    ],
    totalDuration: 96,
    expectedChannels: 2,
    escalationPath: [getBestChannel(stats), 'email']
  };
}

function getBestChannel(stats) {
  const channels = Object.entries(stats)
    .filter(([channel, data]) => data.sent > 0)
    .sort(([,a], [,b]) => b.responseRate - a.responseRate);

  return channels.length > 0 ? channels[0][0] : 'whatsapp';
}

return [{
  json: {
    ...orchestrationData,
    channelAnalysis: channelStats,
    orchestrationStrategy,
    analyzedAt: new Date().toISOString()
  }
}];

// Schedule the orchestration sequence
const data = $json;
const sequence = data.orchestrationStrategy.sequence;
const now = new Date();

const scheduledSteps = sequence.map((step, index) => {
  const scheduleTime = new Date(now.getTime() + step.delay * 60 * 60 * 1000); // delay in hours

  return {
    json: {
      orchestrationId: data.orchestrationId,
      clientId: data.clientId,
      clientName: data.clientName,
      clientEmail: data.clientEmail,
      clientPhone: data.clientPhone,
      stepIndex: index + 1,
      totalSteps: sequence.length,
      channel: step.channel,
      type: step.type,
      priority: step.priority,
      scheduledFor: scheduleTime.toISOString(),
      delayHours: step.delay,
      status: 'SCHEDULED',
      context: data.context,
      targetCountry: data.targetCountry,
      visaType: data.visaType,
      serviceLevel: data.serviceLevel,
      trigger: data.trigger
    }
  };
});

return scheduledSteps;

