// Process payment webhook data
const body = $json.body;
const headers = $json.headers;

// Validate MercadoPago webhook
if (headers['x-signature'] && body.data) {
  // MercadoPago webhook validation logic here
  const paymentData = {
    source: 'MERCADO_PAGO',
    paymentId: body.data.id,
    status: body.action, // payment.created, payment.updated
    type: body.type, // payment
    webhookId: body.id,
    dateCreated: body.date_created,
    rawData: body
  };

  return [{ json: paymentData }];
}

// Validate internal payment system
if (headers['x-webhook-secret'] === 'visa2any_webhook_secret_2024') {
  const paymentData = {
    source: 'INTERNAL',
    clientId: body.clientId,
    paymentId: body.paymentId,
    status: body.status, // PENDING, COMPLETED, FAILED, REFUNDED
    amount: body.amount,
    currency: body.currency || 'BRL',
    method: body.method, // CREDIT_CARD, PIX, BOLETO, BANK_TRANSFER
    installments: body.installments || 1,
    transactionId: body.transactionId,
    clientData: body.clientData || {},
    serviceData: body.serviceData || {},
    createdAt: body.createdAt || new Date().toISOString(),
    rawData: body
  };

  return [{ json: paymentData }];
}

throw new Error('Invalid webhook source or signature');

// Process MercadoPago payment details
const webhookData = $('process-payment-webhook').item.json;
const paymentDetails = $json;

const processedPayment = {
  source: 'MERCADO_PAGO',
  paymentId: paymentDetails.id,
  status: paymentDetails.status, // pending, approved, authorized, in_process, in_mediation, rejected, cancelled, refunded, charged_back
  statusDetail: paymentDetails.status_detail,
  amount: paymentDetails.transaction_amount,
  currency: paymentDetails.currency_id,
  method: paymentDetails.payment_method_id,
  installments: paymentDetails.installments,
  transactionId: paymentDetails.id,
  externalReference: paymentDetails.external_reference, // Our internal ID
  payer: {
    email: paymentDetails.payer.email,
    firstName: paymentDetails.payer.first_name,
    lastName: paymentDetails.payer.last_name,
    phone: paymentDetails.payer.phone?.area_code + paymentDetails.payer.phone?.number,
    identification: paymentDetails.payer.identification
  },
  dates: {
    created: paymentDetails.date_created,
    approved: paymentDetails.date_approved,
    lastUpdated: paymentDetails.date_last_updated
  },
  fees: paymentDetails.fee_details || [],
  rawData: paymentDetails
};

// Try to extract client ID from external reference
if (processedPayment.externalReference) {
  const clientIdMatch = processedPayment.externalReference.match(/CLIENT_(\d+)/);
  if (clientIdMatch) {
    processedPayment.clientId = clientIdMatch[1];
  }
}

return [{ json: processedPayment }];

parameters.jsonBody:
={{ JSON.stringify({
  action: 'find_client_and_payment',
  data: {
    clientId: $json.clientId || '',
    email: $json.payer?.email || $json.clientData?.email || '',
    query: 'SELECT c.*, p.* FROM clients c LEFT JOIN payments p ON c.id = p.client_id WHERE c.id = ? OR c.email = ? ORDER BY p.created_at DESC LIMIT 1'
  }
}) }}

