// Process successful payment
const paymentData = $('process-mercadopago-payment').item?.json || $('process-payment-webhook').item.json;
const clientData = $('find-client-and-payment').item.json;

const successData = {
  clientId: clientData.id,
  clientName: clientData.name,
  clientEmail: clientData.email,
  clientPhone: clientData.phone,
  paymentId: paymentData.paymentId,
  amount: paymentData.amount,
  currency: paymentData.currency,
  method: paymentData.method,
  installments: paymentData.installments,
  transactionId: paymentData.transactionId,
  approvedAt: paymentData.dates?.approved || new Date().toISOString(),
  serviceType: clientData.visa_type || 'UNKNOWN',
  targetCountry: clientData.target_country || 'UNKNOWN',
  totalAmountPaid: (clientData.total_paid || 0) + paymentData.amount,
  processedAt: new Date().toISOString()
};

// Determine next steps based on service type and amount
let nextSteps = [];
let priority = 'HIGH';
let autoTriggers = [];

if (successData.amount >= 3000) {
  nextSteps.push('Ativar atendimento VIP');
  nextSteps.push('Agendar consultoria priorit√°ria');
  nextSteps.push('Enviar boas-vindas premium');
  autoTriggers.push('VIP_ONBOARDING');
  priority = 'URGENT';
} else if (successData.amount >= 1500) {
  nextSteps.push('Ativar sequ√™ncia premium');
  nextSteps.push('Enviar guia completo');
  nextSteps.push('Agendar consultoria');
  autoTriggers.push('PREMIUM_ONBOARDING');
} else {
  nextSteps.push('Ativar sequ√™ncia padr√£o');
  nextSteps.push('Enviar boas-vindas');
  nextSteps.push('Disponibilizar materiais');
  autoTriggers.push('STANDARD_ONBOARDING');
}

// Check if this completes the full payment
const expectedAmount = clientData.service_price || paymentData.amount;
const isFullPayment = successData.totalAmountPaid >= expectedAmount;

if (isFullPayment) {
  nextSteps.push('Iniciar processo de documenta√ß√£o');
  nextSteps.push('Atribuir especialista');
  autoTriggers.push('FULL_PAYMENT_PROCESS_START');
}

return [{
  json: {
    ...successData,
    nextSteps,
    priority,
    autoTriggers,
    isFullPayment,
    paymentSequence: clientData.payment_sequence || 1
  }
}];

