// Prepare success notifications
const data = $json;

const whatsappMessage = `üéâ PAGAMENTO APROVADO! üéâ\n\n` +
  `Ol√° ${data.clientName}!\n\n` +
  `‚úÖ Seu pagamento de ${data.currency} ${data.amount.toLocaleString('pt-BR')} foi aprovado!\n\n` +
  `${data.installments > 1 ? `üí≥ Parcelado em ${data.installments}x\n` : ''}` +
  `üèõÔ∏è M√©todo: ${getPaymentMethodName(data.method)}\n\n` +
  `${data.isFullPayment ? 
    'üöÄ Processo iniciado! Nossa equipe entrar√° em contato em at√© 2 horas\n\nüìã Pr√≥ximos passos enviados por email' :
    'üí∞ Pagamento parcial recebido! Aguardando complemento para iniciar processo'\n\n}` +
  `üìû D√∫vidas? Responda esta mensagem!\n\n` +
  `üåü Obrigado por confiar na Visa2Any!`;

const emailSubject = `üéâ Pagamento Aprovado - ${data.serviceType} para ${data.targetCountry}`;

const teamAlert = `üí∞ *PAGAMENTO APROVADO* üí∞\n\n` +
  `üë§ *Cliente:* ${data.clientName}\n` +
  `üíµ *Valor:* ${data.currency} ${data.amount.toLocaleString('pt-BR')}\n` +
  `üí≥ *M√©todo:* ${getPaymentMethodName(data.method)}\n` +
  `üî¢ *Parcelas:* ${data.installments}x\n` +
  `üåç *Servi√ßo:* ${data.serviceType} - ${data.targetCountry}\n` +
  `üìä *Status:* ${data.isFullPayment ? 'PAGAMENTO COMPLETO ‚úÖ' : 'PAGAMENTO PARCIAL ‚è≥'}\n\n` +
  `üéØ *Pr√≥ximas a√ß√µes:*\n${data.nextSteps.map(step => `‚Ä¢ ${step}`).join('\n')}\n\n` +
  `‚ö° *Prioridade:* ${data.priority}\n` +
  `üÜî *ID Pagamento:* ${data.paymentId}`;

function getPaymentMethodName(method) {
  const methods = {
    'credit_card': 'Cart√£o de Cr√©dito',
    'debit_card': 'Cart√£o de D√©bito',
    'pix': 'PIX',
    'bolbradesco': 'Boleto',
    'bank_transfer': 'Transfer√™ncia Banc√°ria'
  };
  return methods[method] || method;
}

return [{
  json: {
    ...data,
    notifications: {
      whatsappMessage,
      emailSubject,
      teamAlert
    }
  }
}];

// Trigger auto-sequences based on payment success
const data = $json;
const triggers = data.autoTriggers || [];

const automationTriggers = triggers.map(trigger => ({
  json: {
    type: 'automation_trigger',
    trigger: trigger,
    clientId: data.clientId,
    clientData: {
      name: data.clientName,
      email: data.clientEmail,
      phone: data.clientPhone,
      serviceType: data.serviceType,
      targetCountry: data.targetCountry,
      paymentAmount: data.amount,
      isFullPayment: data.isFullPayment,
      priority: data.priority
    },
    context: {
      paymentId: data.paymentId,
      approvedAt: data.approvedAt,
      paymentMethod: data.method
    },
    scheduledFor: new Date(Date.now() + 5 * 60 * 1000).toISOString() // 5 minutes delay
  }
}));

return automationTriggers;

// Process failed/rejected payment
const paymentData = $('process-mercadopago-payment').item?.json || $('process-payment-webhook').item.json;
const clientData = $('find-client-and-payment').item.json;

const failureData = {
  clientId: clientData.id,
  clientName: clientData.name,
  clientEmail: clientData.email,
  clientPhone: clientData.phone,
  paymentId: paymentData.paymentId,
  amount: paymentData.amount,
  currency: paymentData.currency,
  method: paymentData.method,
  status: paymentData.status,
  statusDetail: paymentData.statusDetail,
  rejectionReason: getRejectReason(paymentData.statusDetail),
  suggestion: getSuggestion(paymentData.statusDetail),
  retryPossible: isRetryPossible(paymentData.statusDetail),
  processedAt: new Date().toISOString()
};

function getRejectReason(detail) {
  const reasons = {
    'cc_rejected_insufficient_amount': 'Saldo insuficiente no cart√£o',
    'cc_rejected_bad_filled_card_number': 'N√∫mero do cart√£o incorreto',
    'cc_rejected_bad_filled_date': 'Data de validade incorreta',
    'cc_rejected_bad_filled_security_code': 'C√≥digo de seguran√ßa incorreto',
    'cc_rejected_bad_filled_other': 'Dados do cart√£o incorretos',
    'cc_rejected_blacklist': 'Cart√£o bloqueado',
    'cc_rejected_expired': 'Cart√£o vencido',
    'cc_rejected_high_risk': 'Transa√ß√£o considerada de alto risco'
  };
  return reasons[detail] || 'Falha no processamento';
}

function getSuggestion(detail) {
  const suggestions = {
    'cc_rejected_insufficient_amount': 'Verifique o saldo ou use outro cart√£o',
    'cc_rejected_bad_filled_card_number': 'Confira o n√∫mero do cart√£o',
    'cc_rejected_bad_filled_date': 'Verifique a data de validade',
    'cc_rejected_bad_filled_security_code': 'Confira o CVV do cart√£o',
    'cc_rejected_bad_filled_other': 'Revise todos os dados do cart√£o',
    'cc_rejected_blacklist': 'Entre em contato com seu banco',
    'cc_rejected_expired': 'Use um cart√£o v√°lido',
    'cc_rejected_high_risk': 'Tente novamente ou use outro m√©todo'
  };
  return suggestions[detail] || 'Tente novamente ou use outro m√©todo de pagamento';
}

function isRetryPossible(detail) {
  const retryable = [
    'cc_rejected_bad_filled_card_number',
    'cc_rejected_bad_filled_date', 
    'cc_rejected_bad_filled_security_code',
    'cc_rejected_bad_filled_other',
    'cc_rejected_high_risk'
  ];
  return retryable.includes(detail);
}

return [{ json: failureData }];

