// Analyze each client for proactive intervention opportunities
let clients = [];

// Handle different input formats
try {
  const inputData = $input.all();
  if (inputData && inputData.length > 0) {
    clients = inputData.map(item => item.json || item);
  } else {
    // No clients found - return empty array
    return [];
  }
} catch (error) {
  console.log('Error processing input:', error);
  return [];
}

const alerts = [];
const now = new Date();

for (const client of clients) {
  // Safety checks - ensure client exists and has required properties
  if (!client || !client.id) {
    console.log('Skipping invalid client:', client);
    continue;
  }

  // Safely extract client properties with defaults
  const lastEngagement = client.last_engagement ? new Date(client.last_engagement) : null;
  const lastActivity = client.last_activity ? new Date(client.last_activity) : null;
  const clientCreated = client.created_at ? new Date(client.created_at) : new Date();

  const daysSinceEngagement = lastEngagement ? 
    Math.floor((now - lastEngagement) / (1000 * 60 * 60 * 24)) : 999;

  const daysSinceActivity = lastActivity ? 
    Math.floor((now - lastActivity) / (1000 * 60 * 60 * 24)) : 999;

  const daysSinceCreated = Math.floor((now - clientCreated) / (1000 * 60 * 60 * 24));

  // Safely get status - try different possible field names
  const clientStatus = client.status || client.client_status || 'UNKNOWN';
  const clientStatusFromLog = client.client_status || client.status || 'UNKNOWN';

  let interventionType = null;
  let priority = 'MEDIUM';
  let message = '';
  let recommendedAction = '';

  // RULE 1: No engagement for 5+ days
  if (daysSinceEngagement >= 5 && clientStatusFromLog === 'ACTIVE') {
    interventionType = 'LOW_ENGAGEMENT';
    priority = 'HIGH';
    message = `Cliente ${client.name || 'Unknown'} sem engajamento h√° ${daysSinceEngagement} dias`;
    recommendedAction = 'PERSONALIZED_OUTREACH';
  }

  // RULE 2: Documents pending for 3+ days
  else if (clientStatus === 'DOCUMENTS_PENDING' && daysSinceActivity >= 3) {
    interventionType = 'DOCUMENTS_STALLED';
    priority = 'HIGH';
    message = `Cliente ${client.name || 'Unknown'} com documentos pendentes h√° ${daysSinceActivity} dias`;
    recommendedAction = 'DOCUMENT_ASSISTANCE';
  }

  // RULE 3: Payment pending for 2+ days
  else if (clientStatus === 'PAYMENT_PENDING' && daysSinceActivity >= 2) {
    interventionType = 'PAYMENT_DELAYED';
    priority = 'URGENT';
    message = `Cliente ${client.name || 'Unknown'} com pagamento pendente h√° ${daysSinceActivity} dias`;
    recommendedAction = 'PAYMENT_RECOVERY';
  }

  // RULE 4: New client (< 2 days) without activity
  else if (daysSinceCreated <= 2 && daysSinceActivity >= 1) {
    interventionType = 'NEW_CLIENT_ABANDONED';
    priority = 'HIGH';
    message = `Novo cliente ${client.name || 'Unknown'} sem atividade h√° ${daysSinceActivity} dia(s)`;
    recommendedAction = 'ONBOARDING_ACCELERATION';
  }

  // RULE 5: Long-time client going cold
  else if (daysSinceCreated > 30 && daysSinceEngagement >= 7) {
    interventionType = 'LONG_TERM_DISENGAGEMENT';
    priority = 'MEDIUM';
    message = `Cliente antigo ${client.name || 'Unknown'} se afastando (${daysSinceEngagement} dias sem engajamento)`;
    recommendedAction = 'REACTIVATION_CAMPAIGN';
  }

  if (interventionType) {
    alerts.push({
      json: {
        clientId: client.id,
        clientName: client.name || 'Unknown',
        clientEmail: client.email || '',
        clientPhone: client.phone || '',
        clientStatus: clientStatus,
        interventionType,
        priority,
        message,
        recommendedAction,
        daysSinceEngagement,
        daysSinceActivity,
        daysSinceCreated,
        targetCountry: client.target_country || '',
        visaType: client.visa_type || '',
        detectedAt: now.toISOString()
      }
    });
  }
}

// Sort by priority: URGENT > HIGH > MEDIUM
const priorityOrder = { 'URGENT': 3, 'HIGH': 2, 'MEDIUM': 1 };
alerts.sort((a, b) => priorityOrder[b.json.priority] - priorityOrder[a.json.priority]);

// Log for debugging
console.log(`Processed ${clients.length} clients, found ${alerts.length} alerts`);

return alerts;

