// Extract and validate visa approval data
const body = $json.body;
const headers = $json.headers;

// Validate webhook secret
if (headers['x-webhook-secret'] !== 'visa2any_webhook_secret_2024') {
  throw new Error('Invalid webhook secret');
}

const approvalData = {
  clientId: body.clientId,
  clientName: body.clientName,
  clientEmail: body.clientEmail,
  clientPhone: body.clientPhone,
  visaType: body.visaType,
  targetCountry: body.targetCountry,
  approvalDate: body.approvalDate || new Date().toISOString(),
  totalAmountPaid: body.totalAmountPaid || 0,
  serviceLevel: body.serviceLevel || 'STANDARD', // BASIC, STANDARD, PREMIUM, VIP
  processingTime: body.processingTime || null,
  consultor: body.consultor || null,
  referralSource: body.referralSource || null
};

// Calculate client value score for upsell targeting
let clientValueScore = 0;
if (approvalData.totalAmountPaid >= 5000) clientValueScore += 30;
else if (approvalData.totalAmountPaid >= 3000) clientValueScore += 20;
else if (approvalData.totalAmountPaid >= 1500) clientValueScore += 10;

if (approvalData.serviceLevel === 'VIP') clientValueScore += 25;
else if (approvalData.serviceLevel === 'PREMIUM') clientValueScore += 15;
else if (approvalData.serviceLevel === 'STANDARD') clientValueScore += 5;

if (approvalData.processingTime && approvalData.processingTime <= 30) clientValueScore += 15;

if (['USA', 'CANADA', 'AUSTRALIA', 'UK'].includes(approvalData.targetCountry)) clientValueScore += 20;
else if (['GERMANY', 'FRANCE', 'SPAIN', 'PORTUGAL'].includes(approvalData.targetCountry)) clientValueScore += 15;

return [{ json: { ...approvalData, clientValueScore } }];

// Intelligent upsell opportunity detection
const approvalData = $('process-approval-data').item.json;
const clientProfile = $json;

const upsellOpportunities = [];
let totalUpsellValue = 0;

// 1. FAMILY REUNIFICATION
if (clientProfile.has_dependents && clientProfile.family_members > 0) {
  const familyValue = clientProfile.family_members * 2500; // Average per family member
  upsellOpportunities.push({
    type: 'FAMILY_REUNIFICATION',
    title: 'Reunifica√ß√£o Familiar',
    description: `Traga ${clientProfile.family_members} familiar(es) para ${approvalData.targetCountry}`,
    estimatedValue: familyValue,
    probability: clientProfile.family_members <= 3 ? 85 : 70,
    timeline: '2-6 meses',
    priority: 'HIGH'
  });
  totalUpsellValue += familyValue;
}

// 2. PERMANENT RESIDENCY / CITIZENSHIP
if (clientProfile.interested_in_citizenship || clientProfile.plans_to_immigrate) {
  const citizenshipValue = approvalData.targetCountry === 'USA' ? 8000 : 
                          ['CANADA', 'AUSTRALIA'].includes(approvalData.targetCountry) ? 6000 :
                          ['GERMANY', 'PORTUGAL'].includes(approvalData.targetCountry) ? 5000 : 4000;

  upsellOpportunities.push({
    type: 'CITIZENSHIP_PATHWAY',
    title: 'Caminho para Cidadania',
    description: `Processo completo de cidadania ${approvalData.targetCountry}`,
    estimatedValue: citizenshipValue,
    probability: clientProfile.interested_in_citizenship ? 75 : 50,
    timeline: '12-24 meses',
    priority: 'MEDIUM'
  });
  totalUpsellValue += citizenshipValue;
}

// 3. RELOCATION SERVICES
if (approvalData.clientValueScore >= 50) {
  upsellOpportunities.push({
    type: 'RELOCATION_PACKAGE',
    title: 'Pacote Completo de Reloca√ß√£o',
    description: 'Moradia, escola, trabalho, abertura de conta banc√°ria',
    estimatedValue: 3500,
    probability: 60,
    timeline: '1-3 meses',
    priority: 'MEDIUM'
  });
  totalUpsellValue += 3500;
}

// 4. BUSINESS VISA UPGRADE
if (approvalData.visaType.includes('TOURIST') && clientProfile.budget_range === 'HIGH') {
  upsellOpportunities.push({
    type: 'BUSINESS_VISA',
    title: 'Upgrade para Visto de Neg√≥cios',
    description: 'Possibilidades de investimento e neg√≥cios',
    estimatedValue: 4500,
    probability: 40,
    timeline: '3-6 meses',
    priority: 'LOW'
  });
  totalUpsellValue += 4500;
}

// 5. SECOND COUNTRY VISA
if (approvalData.clientValueScore >= 40) {
  const secondCountryOptions = {
    'USA': ['CANADA', 'UK'],
    'CANADA': ['USA', 'AUSTRALIA'],
    'AUSTRALIA': ['NEW_ZEALAND', 'CANADA'],
    'UK': ['IRELAND', 'PORTUGAL'],
    'GERMANY': ['AUSTRIA', 'SWITZERLAND']
  };

  const secondCountries = secondCountryOptions[approvalData.targetCountry] || ['PORTUGAL', 'SPAIN'];

  upsellOpportunities.push({
    type: 'SECOND_COUNTRY',
    title: 'Segundo Visto - M√∫ltiplas Op√ß√µes',
    description: `Expanda suas possibilidades: ${secondCountries.join(' ou ')}`,
    estimatedValue: 3000,
    probability: 35,
    timeline: '4-8 meses',
    priority: 'LOW'
  });
  totalUpsellValue += 3000;
}

// 6. PREMIUM SUPPORT UPGRADE
if (approvalData.serviceLevel === 'STANDARD' && approvalData.clientValueScore >= 30) {
  upsellOpportunities.push({
    type: 'PREMIUM_SUPPORT',
    title: 'Upgrade para Suporte Premium',
    description: 'Atendimento VIP para futuros processos',
    estimatedValue: 1500,
    probability: 70,
    timeline: 'Imediato',
    priority: 'MEDIUM'
  });
  totalUpsellValue += 1500;
}

// Sort by priority and probability
upsellOpportunities.sort((a, b) => {
  const priorityWeight = { 'HIGH': 3, 'MEDIUM': 2, 'LOW': 1 };
  const scoreA = priorityWeight[a.priority] * a.probability;
  const scoreB = priorityWeight[b.priority] * b.probability;
  return scoreB - scoreA;
});

return [{
  json: {
    ...approvalData,
    clientProfile,
    upsellOpportunities,
    totalUpsellValue,
    upsellScore: Math.min(100, (upsellOpportunities.length * 15) + (approvalData.clientValueScore * 0.5)),
    celebrationCompleted: new Date().toISOString()
  }
}];

// Prepare personalized upsell campaign
const data = $json;
const topOpportunity = data.upsellOpportunities[0];
const secondaryOpportunities = data.upsellOpportunities.slice(1, 3);

// Congratulatory message with strategic upsell
const whatsappMessage = `üéâ PARAB√âNS, ${data.clientName}! Seu visto para ${data.targetCountry} foi aprovado! üá∫üá∏‚úàÔ∏è\n\n` +
  `üåü Que conquista incr√≠vel! Agora √© hora de planejar os pr√≥ximos passos...\n\n` +
  `üí° *OPORTUNIDADE ESPECIAL*\n` +
  `üéØ ${topOpportunity.title}\n` +
  `üìã ${topOpportunity.description}\n` +
  `‚è∞ ${topOpportunity.timeline}\n\n` +
  `${secondaryOpportunities.length > 0 ? 
    `üî• *OUTRAS POSSIBILIDADES:*\n${secondaryOpportunities.map(opp => `‚Ä¢ ${opp.title}`).join('\n')}\n\n` : ''}` +
  `üìû Quer saber mais? Responda "SIM" e nossa equipe entrar√° em contato!\n\n` +
  `üéÅ *OFERTA LIMITADA:* 15% desconto para ex-clientes at√© ${new Date(Date.now() + 15 * 24 * 60 * 60 * 1000).toLocaleDateString('pt-BR')}`;

// Email content with detailed opportunities
const emailData = {
  to: data.clientEmail,
  subject: `üéâ PARAB√âNS! Seu visto foi aprovado + Pr√≥ximas oportunidades`,
  template: 'visa-approved-upsell',
  priority: 'HIGH',
  data: {
    clientName: data.clientName,
    targetCountry: data.targetCountry,
    visaType: data.visaType,
    approvalDate: data.approvalDate,
    topOpportunity: topOpportunity,
    allOpportunities: data.upsellOpportunities,
    totalValue: data.totalUpsellValue,
    celebrationDate: data.celebrationCompleted,
    discountExpiry: new Date(Date.now() + 15 * 24 * 60 * 60 * 1000).toISOString()
  }
};

// Follow-up sequence planning
const followUpSequence = [
  {
    delay: 3, // days
    type: 'EMAIL',
    subject: 'Como Est√° Se Preparando para a Viagem?',
    content: 'tips_preparation',
    cta: 'relocation_services'
  },
  {
    delay: 7, // days  
    type: 'WHATSAPP',
    content: 'family_reunification_offer',
    condition: data.clientProfile.has_dependents
  },
  {
    delay: 14, // days
    type: 'EMAIL',
    subject: 'Planejando Perman√™ncia? Cidadania Pode Ser Sua!',
    content: 'citizenship_pathway',
    condition: data.clientProfile.interested_in_citizenship
  },
  {
    delay: 30, // days
    type: 'PHONE_CALL',
    content: 'personal_consultation',
    priority: 'HIGH'
  }
];

return [{
  json: {
    ...data,
    upsellCampaign: {
      whatsappMessage,
      emailData,
      followUpSequence,
      campaignId: `UPSELL_${data.clientId}_${Date.now()}`,
      expectedRevenue: data.totalUpsellValue * 0.3, // 30% conversion estimate
      campaignStarted: new Date().toISOString()
    }
  }
}];

// Schedule follow-up sequence
const data = $json;
const followUps = data.upsellCampaign.followUpSequence;

const scheduledTasks = followUps.map((followUp, index) => {
  // Check conditions
  if (followUp.condition && !followUp.condition) {
    return null; // Skip this follow-up
  }

  const scheduleDate = new Date(Date.now() + followUp.delay * 24 * 60 * 60 * 1000);

  return {
    json: {
      clientId: data.clientId,
      campaignId: data.upsellCampaign.campaignId,
      sequenceStep: index + 1,
      type: followUp.type,
      scheduledFor: scheduleDate.toISOString(),
      content: followUp.content,
      subject: followUp.subject || null,
      cta: followUp.cta || null,
      priority: followUp.priority || 'MEDIUM',
      status: 'SCHEDULED'
    }
  };
}).filter(Boolean);

return scheduledTasks;

