const body = $json.body;
const headers = $json.headers;

if (headers['x-webhook-secret'] !== 'visa2any_webhook_secret_2024') {
  throw new Error('Invalid webhook secret');
}

const waitlistData = {
  clientId: body.clientId,
  clientName: body.clientName,
  clientEmail: body.clientEmail,
  clientPhone: body.clientPhone,
  country: body.country,
  consulate: body.consulate,
  visaType: body.visaType,
  currentAppointmentDate: body.currentAppointmentDate,
  preferredDateStart: body.preferredDateStart,
  preferredDateEnd: body.preferredDateEnd,
  priority: parseInt(body.priority) || 2,
  notificationMethods: body.notificationMethods || ['whatsapp', 'email'],
  maxNoticeHours: parseInt(body.maxNoticeHours) || 24,
  signupSource: body.source || 'WEBSITE',
  createdAt: new Date().toISOString()
};

const requiredFields = ['clientId', 'clientName', 'country', 'consulate', 'visaType'];
for (const field of requiredFields) {
  if (!waitlistData[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

return [{ json: waitlistData }];

const signupData = $('validate-waitlist-signup').item.json;
const isNew = $('is-new-signup').item.json.id ? false : true;

const confirmationMessage = `‚úÖ *WAITLIST CONFIRMADA!* ‚úÖ\n\n` +
  `Ol√° ${signupData.clientName}!\n\n` +
  `${isNew ? 'üÜï Voc√™ foi adicionado' : 'üîÑ Suas prefer√™ncias foram atualizadas'} na nossa waitlist para:\n\n` +
  `üèõÔ∏è *Consulado:* ${signupData.consulate}\n` +
  `üåç *Pa√≠s:* ${signupData.country}\n` +
  `üìã *Tipo de Visto:* ${signupData.visaType}\n\n` +
  `üìÖ *Per√≠odo Preferido:*\n` +
  `De: ${signupData.preferredDateStart ? new Date(signupData.preferredDateStart).toLocaleDateString('pt-BR') : 'Qualquer data'}\n` +
  `At√©: ${signupData.preferredDateEnd ? new Date(signupData.preferredDateEnd).toLocaleDateString('pt-BR') : 'Qualquer data'}\n\n` +
  `‚ö° *Prioridade:* ${getPriorityText(signupData.priority)}\n` +
  `‚è∞ *Aviso m√≠nimo:* ${signupData.maxNoticeHours}h\n\n` +
  `üîî *Como voc√™ ser√° notificado:*\n${signupData.notificationMethods.map(method => getMethodEmoji(method) + ' ' + getMethodName(method)).join('\n')}\n\n` +
  `ü§ñ *Sistema ativo:* Monitoramos cancelamentos a cada 2 minutos\n` +
  `‚ö° *Resposta r√°pida:* Voc√™ ser√° notificado instantaneamente\n\n` +
  `üìû D√∫vidas? Responda esta mensagem!`;

function getPriorityText(priority) {
  const priorities = {
    1: 'Baixa (notifica√ß√£o em 1h)',
    2: 'M√©dia (notifica√ß√£o em 15min)', 
    3: 'Alta (notifica√ß√£o em 5min)',
    4: 'Urgente (notifica√ß√£o imediata)'
  };
  return priorities[priority] || 'M√©dia';
}

function getMethodEmoji(method) {
  const emojis = {
    'whatsapp': 'üì±',
    'email': 'üìß',
    'sms': 'üí¨',
    'phone': 'üìû'
  };
  return emojis[method] || 'üì±';
}

function getMethodName(method) {
  const names = {
    'whatsapp': 'WhatsApp',
    'email': 'Email',
    'sms': 'SMS',
    'phone': 'Liga√ß√£o'
  };
  return names[method] || 'WhatsApp';
}

const emailSubject = `‚úÖ Waitlist Confirmada - ${signupData.consulate}`;

return [{
  json: {
    ...signupData,
    isNewSignup: isNew,
    confirmationMessage,
    emailSubject
  }
}];

