// Process WhatsApp trigger data
const data = $json.body || $json;

// Validate required fields
if (!data.clientId || !data.message || !data.phone) {
  throw new Error('Missing required fields: clientId, message, phone');
}

// Format phone number for Brazil
let phone = data.phone.toString().replace(/\D/g, '');
if (phone.length === 11 && phone.startsWith('11')) {
  phone = '55' + phone;
} else if (phone.length === 10) {
  phone = '5511' + phone;
} else if (!phone.startsWith('55')) {
  phone = '55' + phone;
}

const whatsappData = {
  clientId: data.clientId,
  clientName: data.clientName || 'Cliente',
  clientEmail: data.clientEmail || 'no-email@visa2any.com',
  phone: phone,
  message: data.message,
  template: data.template || null,
  variables: data.variables || {},
  priority: data.priority || 'MEDIUM',
  processedAt: new Date().toISOString(),
  messageId: `wa_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
};

console.log('ðŸ“± WhatsApp message prepared:', whatsappData.messageId);
console.log('To:', whatsappData.phone);
console.log('Message preview:', whatsappData.message.substring(0, 50) + '...');

return [{ json: whatsappData }];

