// Log WhatsApp result
const whatsappData = $('process-whatsapp-data').item.json;
const result = $json;

const logData = {
  type: 'WHATSAPP_SENT',
  action: 'send_whatsapp_via_n8n',
  details: JSON.stringify({
    messageId: whatsappData.messageId,
    phone: whatsappData.phone,
    template: whatsappData.template,
    success: result.success || false,
    queued: result.data?.queued || false,
    apiResponse: result
  }),
  success: result.success || false,
  clientId: whatsappData.clientId,
  error: result.success ? null : (result.error || 'Unknown error'),
  executedAt: new Date().toISOString()
};

console.log('ðŸ“Š WhatsApp result logged:', {
  success: logData.success,
  messageId: whatsappData.messageId,
  phone: whatsappData.phone
});

return [{ json: logData }];

parameters.jsonBody:
={{ JSON.stringify({
